package com.hotale.entity;

public class Comment {
	
	private int order_id;
	private int hotel_id;
	private float comment_mark;
	private String comment_detail;
	
	
	
	public Comment() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getOrder_id() {
		return order_id;
	}
	public void setOrder_id(int order_id) {
		this.order_id = order_id;
	}
	public int getHotel_id() {
		return hotel_id;
	}
	public void setHotel_id(int hotel_id) {
		this.hotel_id = hotel_id;
	}
	public float getComment_mark() {
		return comment_mark;
	}
	public void setComment_mark(float comment_mark) {
		this.comment_mark = comment_mark;
	}
	public String getComment_detail() {
		return comment_detail;
	}
	public void setComment_detail(String comment_detail) {
		this.comment_detail = comment_detail;
	}


}
